<template>
  <v-container fill-height fluid>
    <v-row align="center" justify="center">
      <v-col cols="12" align="center">
        <v-img contain :src="srcImageBase64" height="280" width="400" />
        <h1 class="text-center">
          {{ text }}
        </h1>
        <v-btn rounded outlined class="mt-5" to="/">
          {{ $t('TAKE ME BACK HOME') }}
        </v-btn>
      </v-col>
    </v-row>
  </v-container>
</template>

<script>
export default {
  props: {
    error: {
      type: Object,
      default: null
    }
  },

  layout({ $auth }) {
    return $auth.loggedIn ? 'apps' : 'default';
  },

  head() {
    return {
      title: this.text
    };
  },

  computed: {
    srcImage() {
      if (this.error.statusCode === 403) {
        return this.$vuetify.theme.dark ? '/img/403-light.webp' : '/img/403-dark.webp';
      } else if (this.error.statusCode === 404) {
        return this.$vuetify.theme.dark ? '/img/404-light.webp' : '/img/404-dark.webp';
      } else {
        return this.$vuetify.theme.dark ? '/img/500-light.webp' : '/img/500-dark.webp';
      }
    },

    srcImageBase64() {
      if (this.error.statusCode === 403) {
        return this.$vuetify.theme.dark
          ? 'data:image/webp;base64,'
          : 'data:image/webp;base64,';
      } else if (this.error.statusCode === 404) {
        return this.$vuetify.theme.dark
          ? 'data:image/webp;base64,'
          : 'data:image/webp;base64,';
      } else {
        return this.$vuetify.theme.dark
          ? 'data:image/webp;base64,'
          : 'data:image/webp;base64,';
      }
    },

    text() {
      if (this.error.statusCode === 403) {
        return this.$t('oops-access-to-the-requested-resource-is-denied');
      } else if (this.error.statusCode === 404) {
        return this.$t('oops-that-page-can-t-be-found');
      } else {
        return this.$t('oops-internal-server-error');
      }
    }
  }
};
</script>

<style scoped>
h1 {
  font-size: 20px;
}
</style>
